mapper.select:
select * from eq_pathology_review

    REVIEW_NO=#{reviewno}
    and APPLI_NO=#{applino.applino}
    and REVIEW_RESULT=#{reviewresult}
    and DIAGNOSIS_INITIAL=#{diagnosisinitial}
    and REVIEW_STATUS=#{reviewstatus}
    and REVIEW_NOTE=#{reviewnote}
    and REVIEW_DATE=#{reviewdate}
    and DIAGNOSIS_STATUS=#{diagnosisstatus}
    and CONSULTATION_IF=#{consultationif}
    and REVIEW_DOCTOR_ID=#{reviewdoctorid.id}

mapper.update:
update eq_pathology_review
APPLI_NO=#{applino.applino}, REVIEW_RESULT=#{reviewresult}, DIAGNOSIS_INITIAL=#{diagnosisinitial}, REVIEW_STATUS=#{reviewstatus}, REVIEW_NOTE=#{reviewnote}, REVIEW_DATE=#{reviewdate}, DIAGNOSIS_STATUS=#{diagnosisstatus}, CONSULTATION_IF=#{consultationif}, REVIEW_DOCTOR_ID=#{reviewdoctorid.id},
where REVIEW_NO=#{reviewno}

mapper.select:
select PR.REVIEW_NO as reviewno,PI.PATIENT_NAME as patientname,
PA.APPLI_NO as applino,PA.PRODUCTS_NUM as productsnum,
PI.INFOR_CLINICAL as inforclinical,PR.REVIEW_DATE as reportdate,
PA.INSP_METHOD_LIST as inspmethodlist,PR.REVIEW_RESULT as reviewresult,
PR.REVIEW_DOCTOR_ID as reviewdoctorid,PR.CONSULTATION_IF as consultationif,
PR.DIAGNOSIS_STATUS as diagnosisstatus
from eq_pathology_review PR
left join eq_patient_appli PA on PR.APPLI_NO = PA.APPLI_NO
left join eq_patient_info PI on PI.PATIENT_NO = PA.PATIENT_NO

    PR.DIAGNOSIS_STATUS=#{diagnosisstatus}
    and PR.CONSULTATION_IF=#{consultationif}
    or PI.PATIENT_NAME=#{patientname}
    or PA.APPLI_NO=#{applino,jdbcType=INTEGER}
